Ext.define('AdvaSoftLogin.view.sessioni.SessioniViewController', {
    extend: 'Ext.app.ViewController',
    alias: 'controller.sessionisessioni',

    onCancellaSessione: function(view, rowIndex, colIndex, item, e, record, row) {
        var store = view.getStore();
        var record = view.getStore().getAt(rowIndex);
        store.remove(record);
        store.sync();
    },

    onSessioniActivate: function(component, eOpts) {
        var store = AdvaSoftLogin.app.getStore("Sessioni");
        store.load();
    }

});
